import React, { useState } from "react"
import axios from "axios"

import { Link } from 'react-router-dom';
// import { useNavigate } from "react-router-dom";

function LoginStud(){
    const [responseData, setResponseData] = useState(null);
    const [formdata,setdata]=useState({
        Id:'',
        Passw:''

    })
    // const navigate = useNavigate();

    
    //     axios.post('http://localhost:3002/getlogin', formdata)
    //         .then(response => {
    //             console.log("fgxfxf");
    //             console.log("Response from backend:", response.data);
    //             const statusCode = response.status;
    //             console.log("Status code:", statusCode);
    //             if (statusCode === 200) {
    //                 console.log("login succesfull");
    //             }else if (statusCode === 401) {
    //                 console.log("Redirecting to signup page");
    //                 navigate('http://localhost:3000/');
    //             } else {
    //                 console.log("Internal error");
    //             }
    //         })
    //         .catch(error => {
    //             // Handle error
    //             if (error.response) {
    //               // The request was made and the server responded with a status code
    //               console.error('Server responded with non-2xx status:', error.response.status);
    //               console.error('Error data:', error.response.data);
    //             } else if (error.request) {
    //               // The request was made but no response was received
    //               console.error('No response received:', error.request);
    //             } else {
    //               // Something happened in setting up the request that triggered an error
    //               console.error('Error setting up request:', error.message);
    //             }
    //           });
    // }

    async function handleSubmit(event) {
        event.preventDefault();
        
        try {
            const response = await axios.post('http://localhost:3002/getlogin', formdata);
            console.log("sakjfgjh")
            const statusCode = response.status;
     
            if (statusCode === 200) {
                console.log("login successfull");
            } else if (statusCode === 401) {
                
                console.log("Redirecting to signup page");
                window.location.href = 'http://localhost:3000/';
            } else {
                console.log("Internal error");
            }
        } catch (error) {
            console.error("Error:", error);
            console.log()
            // Handle errors
        }
    }
  
    
    
    const HandleEvent=(event)=>{
        const {name ,value}=event.target;
        setdata({...formdata,[name]:value})
    }
    return (
        <div className="outer">
            
            <div className="inner">
                <div className="head"><h3>Login Form </h3></div>
                <div className="info">
                <form onSubmit={handleSubmit}>
                <label>StudentID:
                    <input type="number" placeholder="StudentId" name="Id" value={formdata.Id} onChange={HandleEvent}/>
                </label>
                <label>Password:
                    <input type="password" placeholder="Password" name="Passw" value={formdata.Passw} onChange={HandleEvent}/>
                </label>
                <label>
                    <input type='submit'/>
                </label>
                </form>
                <Link to={'/'}><a>Create a new account</a></Link>
                </div>
            </div>
        </div>
    )
}
export default LoginStud;
