import React, { useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import Profile from "./Profile"; // Import the Profile component

function LoginStud() {
    const [userProfile, setUserProfile] = useState(null);
    const [formdata, setdata] = useState({
        Id: '',
        Passw: ''
    });
    const history = useHistory();

    async function handleSubmit(event) {
        event.preventDefault();
        
        try {
            const response = await axios.post('http://localhost:3002/getlogin', formdata);
            console.log("Response from backend ", response.data);
            const statusCode = response.status;
    
            if (statusCode === 200) {
                console.log("Login successful");
                localStorage.setItem('token', response.data.token);

                // Fetch user profile information after successful login
                const profileResponse = await axios.get('http://localhost:3002/profile', {
                    headers: {
                        Authorization: `Bearer ${response.data.token}`
                    }
                });

                setUserProfile(profileResponse.data); // Set user profile state
            } else if (statusCode === 401) {
                console.log("Redirecting to signup page");
                window.location.href = 'http://localhost:3000/';
            } else {
                console.log("Internal error");
            }
        } catch (error) {
            console.error("Error:", error);
            if (error.response && error.response.status === 401) {
                console.log("Redirecting to signup page");
                window.location.href = 'http://localhost:3000/';
            } else {
                console.log("Other error occurred");
                // Handle other errors
            }
        }
    }
    
    const HandleEvent = (event) => {
        const {name ,value} = event.target;
        setdata({...formdata, [name]:value});
    };

    return (
        <div className="outer">
            <div className="inner">
                <div className="head"><h3>Login Form </h3></div>
                <div className="info">
                    <form onSubmit={handleSubmit}>
                        <label>StudentID:
                            <input type="number" placeholder="StudentId" name="Id" value={formdata.Id} onChange={HandleEvent}/>
                        </label>
                        <label>Password:
                            <input type="password" placeholder="Password" name="Passw" value={formdata.Passw} onChange={HandleEvent}/>
                        </label>
                        <label>
                            <input type='submit'/>
                        </label>
                    </form>
                    <Link to={'/'}><a>Create a new account</a></Link>
                </div>
            </div>
            {userProfile && <Profile userProfile={userProfile} />} {/* Render Profile component if userProfile is available */}
        </div>
    );
}

export default LoginStud;
