import React, { useState } from "react"
import axios from "axios"
import { Link } from 'react-router-dom';

function SignStud(){
    const [formdata,setdata]=useState({
        name:'',
        StudentId:'',
        Contact:'',
        Course:'',
        Password:''

    })
    const handleSubmit = async (event) => {
        event.preventDefault();
        
        try {
            // Make POST request to backend API
            const response = await axios.post('http://localhost:3002/getinfo', formdata);
            console.log("Response from backend:", response.data);
            // Optionally, do something with the response
        } catch (error) {
            console.error("Error:", error);
            // Handle errors
        }
    };
    const HandleEvent=(event)=>{
        const {name,value}=event.target;
        setdata({...formdata,[name]:value})
    }
    return (
         
        <div className="outer">
            <div className="innerleft">
                 <div className="image"></div>
            </div>
            <div className="innerright">
                <div className="head"><h3>Student Registeration Form</h3></div>
                
                <form onSubmit={handleSubmit}>
                <div className="one"> 
                    <label>StudentID:
                        <input type="number" placeholder="StudentId" name="StudentId" value={formdata.StudentId} onChange={HandleEvent}/>
                    </label>
                </div>
                <div className="">
                <label>Name:
                <input type="text" placeholder="name" name="name" value={formdata.name} onChange={HandleEvent}/>
                </label>
                </div>
                <div>
                <label>Contact:
                    <input type="number" placeholder="Phone no." name="Contact" value={formdata.Contact} onChange={HandleEvent}/>
                </label>
                </div>
                <div>
                <label>Course:
                    <input type="text" placeholder name="Course" value={formdata.Course} onChange={HandleEvent}/>
                </label>
                </div>
                <div>
                <label>Password:
                    <input type="password" placeholder name="Password" value={formdata.Password} onChange={HandleEvent}/>
                </label>
                </div>
                <div>
                <label>
                    <input type='submit'/>
                </label>
                </div>

                </form>
                <Link to={'/login'}><a>Already a user</a></Link>
                
                
            </div>
        </div>
   
    )
}
export default SignStud;
